                (1, 'Message Send', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}},</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">You have sent your appreciation to {{recipientName}} for being a great help at work and have awarded {{recipientName}} with {{linkingVerb}} {{badgeType}} badge with the following message:</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{messageContent}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Thank you for recognising the hard work and effort put in by our people who have gone the extra mile!</span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-6.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-6.jpg\"></figure>', '', 'null', 'A little appreciation goes a long way! Your encouragement is sure to make someone’s day.', 'Dear {{name}},', 'You have sent your appreciation to {{recipientName}} for being a great help at work and have awarded {{recipientName}} with {{linkingVerb}} {{badgeType}} badge with the following message:', 'Thank you for recognising the hard work and effort put in by our people who have gone the extra mile!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:13:48', NULL),
                (2, 'Message Received', '<p>Dear {{name}} ,</p><p>&nbsp;</p><p>{{senderName}} recognises the great help you have been at work and has sent you a message and awarded you with {{linkingVerb}} {{badgeType}} &nbsp;badge with the following message:</p><p>&nbsp;</p><p>{{messageContent}}&nbsp;</p><p>&nbsp;</p><p>Log on to {{ACE}} and check out the {{ACE e-Store}} to redeem the item of your choice today!</p><p>&nbsp;</p><p><strong>Appreciate others</strong></p><p>&nbsp;</p><p>Have a colleague in mind whom you would like to thank?</p><p>&nbsp;</p><p>Show your appreciation today by sending them a message or rewarding them with your <strong>“Recognise Others”</strong> tokens! It’s a really simple way to say thank you.</p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-4-v2.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-4-v2.jpg\"></figure>', '', NULL, 'Excellent work! :name has awarded you with a new badge!', 'Dear {{name}},', '{{senderName}} recognises the great help you have been at work and has sent you a message and awarded you with {{linkingVerb}} {{badgeType}} badge with the following message:', 'Log on to {{ACE}} and check out the {{ACE e-Store}} to redeem the item of your choice today!', 'Appreciate others', 'Appreciate others', 'Show your appreciation today by sending them a message or rewarding them with your', '“Recognise Others”', 'tokens! It’s a really simple way to say thank you.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-17 23:18:36', NULL),
                (3, 'Message Send with Token', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}},</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">You have sent your appreciation to {{recipientName}} for being a great help at work and have awarded {{recipientName}} with {{linkingVerb}} {{badgeType}} badge with the following message:</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{messageContent}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">In addition, you have awarded {{recipientName}} with {{token}} <strong>\"Recognise Others\"</strong> tokens.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Thank you for recognising the hard work and effort put in by our people who have gone the extra mile!</span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-7.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-7.jpg\"></figure>', '', NULL, 'A little appreciation goes a long way! Your encouragement is sure to make someone’s day.', 'Dear {{name}},', 'You have sent your appreciation to {{recipientName}} for being a great help at work and have awarded {{recipientName}} with {{linkingVerb}} {{badgeType}} badge with the following message:', 'In addition, you have awarded {{recipientName}} with {{token}}', '\"Recognise Others\"', 'tokens', 'Thank you for recognising the hard work and effort put in by our people who have gone the extra mile!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:14:02', NULL),
                (4, 'Message Received with Token', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}},</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{senderName}} recognises the great help you have been at work and has sent you a message and awarded you with {{linkingVerb}} {{badgeType}} badge with the following message:</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{messageContent}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">In addition, you have also received {{token}} \"<strong>My Rewards</strong>\" tokens from {{senderName}}.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Log on to {{ACE}} and check out the {{ACE e-Store}} to redeem the item of your choice today!</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Appreciate others</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Have a colleague in mind whom you would like to thank?</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">&nbsp;Show your appreciation today by sending them a message or rewarding them with your “<strong>Recognise Others</strong>” tokens! It’s a really simple way to say thank you.</span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-5-v2.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-5-v2.jpg\"></figure>', '', NULL, 'Excellent work! :name has awarded you with a new badge!', 'Dear {{name}},', '{{senderName}} recognises the great help you have been at work and has sent you a message and awarded you with {{linkingVerb}} {{badgeType}} badge with the following message:', 'In addition, you have also received {{token}}', '\"My Rewards\"', 'tokens from {{senderName}}.', 'Log on to {{ACE}} and check out the {{ACE e-Store}} to redeem the item of your choice today!', 'Appreciate others', 'Have a colleague in mind whom you would like to thank?', 'Show your appreciation today by sending them a message or rewarding them with your', '“Recognise Others”', 'tokens! It’s a really simple way to say thank you.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:14:24', NULL),
                (5, 'Welcome Mail', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Welcome to Deloitte!</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">We are delighted to share with you more information about ACE (Appreciate. Celebrate. Elevate.) – our online platform designed especially for us to appreciate our colleagues for their good work and to celebrate the people who have gone the extra mile to help us.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">On ACE, you can write personalised thank-you messages to colleagues across levels and businesses, and award them achievement badges and tokens. Your colleagues can also do the same for you.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">These tokens can be used to redeem special treats and gifts at the {{ACE e-Store}}.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">&nbsp;As a welcome gift, you will receive {{numberOfToken}} “My Rewards” tokens in your account. Live our Deloitte values and start accumulating “My Rewards” tokens to redeem the item of your choice today!</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">You will find that you also have {{numberOfBlackToken}} “Recognise Others” tokens in your account that you can give out in appreciation of your colleagues.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">To find out how you can navigate ACE, please refer to the FAQ {{herefaq}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Click {{here}}to access ACE now!</span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-2.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-3-v2.jpg\"></figure>', '', NULL, 'Introducing ACE! A platform specially designed for us to appreciate each other better!', 'Welcome to Deloitte!', 'We are delighted to share with you more information about ACE (Appreciate. Celebrate. Elevate.) – our online platform designed especially for us to appreciate our colleagues for their good work and to celebrate the people who have gone the extra mile to help us.', 'On ACE, you can write personalised thank-you messages to colleagues across levels and businesses, and award them achievement badges and tokens. Your colleagues can also do the same for you.', 'These tokens can be used to redeem special treats and gifts at the {{ACE e-Store}}.', 'As a welcome gift, you will receive {{numberOfToken}} “My Rewards” tokens in your account. Live our Deloitte values and start accumulating “My Rewards” tokens to redeem the item of your choice today!', 'You will find that you also have {{numberOfBlackToken}} “Recognise Others” tokens in your account that you can give out in appreciation of your colleagues.', 'To find out how you can navigate ACE, please refer to the FAQ {{herefaq}}.', 'Click {{here}} to access ACE now!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:14:45', NULL),
                (6, 'Monthly Summary Mail', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Hi {{name}},</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">This is the latest summary of your ACE account.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Personal growth achievements</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{currentTier}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{pointsToNextTier}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{personal_growth_achievement_table}}</span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Balance tokens:</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{balance_tokens_table}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Your \"Recognise Others\" tokens</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">For the month of {{month}}, You gave out a total of {{recognizeOthersTokenUsed}} \"Recognise Others\" tokens.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">You have {{recognizeOthersToken}} \"Recognise Others\" tokens remaining, which will expire on {{recognizeOthersTokenExpiration}}.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Remember, you can use ACE to show your appreciation to your colleagues. A small gesture goes a long way!</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Your \"My Rewards\" tokens</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">For the month of {{month}}, you received {{myRewardsToken}} “My Rewards” tokens from your colleagues and you redeemed a total of {{myRewardsTokenUsed}} tokens from the ACE Redemption Store.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">You have a total of {{myRewardsToken}} \"My Rewards\" tokens.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{tokenexpirationprompt}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Be sure to exchange these tokens for exciting gifts available on the {{ACE e-Store}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">To view a full summary of your ACE account, click {{herehistorylink}}.</span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Click {{here_acelink}} to access ACE now!</span></p><p>&nbsp;</p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-1.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-1.jpg\"></figure>', '', NULL, 'Your ACE portal activity summary for the month of :month is ready for viewing!', 'Hi {{name}},', 'This is the latest summary of your ACE account.', 'Personal growth achievements', 'You are currently a {{currentTier}}!', 'Continue to keep up the good work as you are just {{pointsToNextTier}} points away from becoming a {{nextTier}}!', 'Received badges this month', 'World<em>Class</em> Citizen', 'Count:', 'Shared badges this month', 'Balance tokens', '\"Recognise Others\" tokens', '\"My Rewards\" tokens', 'Your \"Recognise Others\" tokens', 'For the month of {{month}}, You gave out a total of {{recognizeOthersTokenUsed}} \"Recognise Others\" tokens.', 'You have {{recognizeOthersToken}} \"Recognise Others\" tokens remaining, which will expire on {{recognizeOthersTokenExpiration}}.', 'Remember, you can use ACE to show your appreciation to your colleagues. A small gesture goes a long way!', 'Your \"My Rewards\" tokens', 'For the month of {{month}}, you received {{myRewardsToken}} “My Rewards” tokens from your colleagues and you redeemed a total of {{myRewardsTokenUsed}} tokens from the ACE Redemption Store.', 'You have a total of {{myRewardsToken}} \"My Rewards\" tokens.', '{{myRewardsTokenExpirationAmount}} tokens will expire on {{myRewardsTokenExpiration}}.', 'Be sure to exchange these tokens for exciting gifts available on the {{ACE e-Store}}!', 'To view a full summary of your ACE account, click {{herehistorylink}}.', 'Click {{here}} to access ACE now!', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:15:06', NULL),
                (7, 'Redemption Mail', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Thank you for shopping at ACE. Here are the details of your redemption and collection:</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Order Number:</strong> &nbsp;{{orderNumber}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Order Date:</strong> {{orderDate}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{orderItems_table}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>\"My Rewards\"</strong> tokens balance: &nbsp;{{remainingToken}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">&nbsp;Please collect your items from us on:</span></p><p>&nbsp;</p><p><span style=\"color:hsl(150,75%,60%);font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Collection date/time:</strong></span><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"> &nbsp;{{collectionDate}}</span></p><p>&nbsp;</p><p><span style=\"color:hsl(150,75%,60%);font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Collection location:</strong></span><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"> D Lounge, Level 30</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">If you are unable to pick up your item/s at the above date and time, please ensure that you appoint a colleague to collect on your behalf. Your colleague must present this redemption email and a photo of your staff pass to redeem the item.</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">To view your redemption history to date, please click {{here}}.</span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome-banner-3-v2.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-3-v2.jpg\"></figure>', '', NULL, 'Your redemption item(s) is/are ready for collection!', 'Dear {{name}},', 'Thank you for shopping at ACE. Here are the details of your redemption and collection:', 'Order Number:', 'Order Date:', 'Product:', 'Quantity:', 'Tokens used:', '\"My Rewards\"', 'tokens balance:  {{remainingToken}}', 'Please collect your items from us on:', 'Collection date/time:', 'Collection location:', 'D Lounge, Level 30', 'If you are unable to pick up your item/s at the above date and time, please ensure that you appoint a colleague to collect on your behalf. Your colleague must present this redemption email and a photo of your staff pass to redeem the item.', 'To view your redemption history to date, please click {{here}}.', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:15:44', NULL),
                (8, 'Tier Promotion Mail', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Congratulations on becoming a {{newbadge}}&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Your great work and effort have been recognised and you are on your way to becoming a {{nextbadge}}!&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">In addition, you have&nbsp; received {{rewardstoken}} “<strong>My Rewards</strong>” tokens for advancing to the next position on your Personal Growth Achievement tier.&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Continue to keep up the good work!&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Log on to ACE and check out the ACE e-Store to redeem the item of your choice today!&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\"><strong>Appreciate others</strong></span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Have a colleague in mind whom you would like to thank?&nbsp;</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Show your appreciation today by sending them a message or rewarding them with your “<i><strong>Recognise Others</strong></i>” tokens! It’s a really simple way to say thank you.</span></p><p>&nbsp;</p><p><span style=\"color:hsl(0,0%,30%);font-family:Calibri, Verdana, Geneva, sans-serif;\"><i>Please do not reply to this email. For any questions, please submit your enquiry or refer to the FAQ {{here}}</i></span></p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome_banner_default.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-4-v2.jpg\"></figure>', '', NULL, 'Excellent work! :name you have been promoted to a new Tier', 'Dear {{name}},', 'Badge Promotion level went up from {{oldbadge}} to {{newbadge}}  has been granted , {{rewardstoken}} rewards token has been  granted to your account.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-07-05 13:15:33', '2020-11-19 05:16:00', NULL),
                (9, 'Mass Token Upload Mail', '<p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">Dear {{name}} ,</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{message}}</span></p><p>&nbsp;</p><p><span style=\"font-family:Calibri, Verdana, Geneva, sans-serif;\">{{masstokenupadetable}}</span></p><p>&nbsp;</p><p>&nbsp;</p>', 'https://www.deloitte.com/', 'https://www.deloitte.com/', '<p>Singapore | {{date}}</p>', 'img/edm/main-banner.jpg', 'img/edm/welcome_banner_default.jpg', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/main-banner.jpg\"></figure>', '<figure class=\"image\"><img src=\"https://deloitte-backend.nmgdev.com/public/img/edm/welcome-banner-default.jpg\"></figure>', '', '', 'Mass Token Upload', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '2020-10-11 13:39:33', '2020-11-19 05:16:18', NULL),
